var client = new HttpClient();
var request = new HttpRequestMessage
{
    Method = HttpMethod.Get,
    RequestUri = new Uri("https://api.wassenger.com/v1/webhooks"),
    Headers =
    {
        { "Token", "<api token goes here>" },
    },
};
